import './App.css';
import {useState} from "react";
import Axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';


function App() {

  const [usuario,setUsuario] = useState("");
  const [contrasenia,setConstrasenia] = useState("");

  const [personaList,setPersonas] = useState([]);

  const send = () => {
    Axios.post("http://localhost:3001/verificar", {
        usuario: usuario,
        contrasenia: contrasenia
    })
    .then(() => {
        alert("Sesión Iniciada");
    })
    .catch((error) => {
        console.error("Error en la autenticación:", error);
        alert("Credenciales incorrectas");
    });
  }

  const getPersonas = () =>{
    Axios.get("http://localhost:3001/reporte").then((response)=>{
      setPersonas(response.data);
    });
  }

  return (
    <div class="container">
    <div className="App">
      <h1 className="titulo">Sistema Cine</h1>
      <div className ="datos">
        <label>Usuario: <input 
        onChange={(event)=>{
          setUsuario(event.target.value);
        }}
        type="text"/></label>
        <label>Contraseña: <input 
        onChange={(event)=>{
          setConstrasenia(event.target.value);
        }}
        type="password"/></label>
        <button className='btn btn-success' onClick={send}>Enviar</button>
      </div>
        <div className='lista'>
          <button onClick={getPersonas}>Listar</button>
          {
            personaList.map((val,key)=>{
              return <div className=''>{val.nombres}</div>
            })
          }
        </div>
    </div>
    <div class="card">
      <div class="card-header">
        Featured
      </div>
      <div class="card-body">
        <h5 class="card-title">Special title treatment</h5>
        <p class="card-text">With supporting text below as a natural lead-in to additional content.</p>
        <a href="#" class="btn btn-primary">Go somewhere</a>
      </div>
    </div>
    </div>
  );
}

export default App;
