import './App.css';
import {useState} from "react";
import Axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';


function App() {

  const [usuario,setUsuario] = useState("");
  const [contrasenia,setConstrasenia] = useState("");

  const [personaList,setPersonas] = useState([]);

  const send = () => {
    Axios.post("http://localhost:3001/verificar", {
        usuario: usuario,
        contrasenia: contrasenia
    })
    .then(() => {
        alert("Sesi贸n Iniciada");
    })
    .catch((error) => {
        console.error("Error en la autenticaci贸n:", error);
        alert("Credenciales incorrectas");
    });
  }

  const getPersonas = () =>{
    Axios.get("http://localhost:3001/reporte").then((response)=>{
      setPersonas(response.data);
    });
  }

  return (
    <div className="container">
      <div className="App">
        <h1 className="titulo">Sistema Cine</h1>
      </div>

      <div className="card text-center">
        <div className="card-header">
          Iniciar sesion
        </div>

        <div className="card-body">
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Usuario: </span>
            <input type="text" 
            onChange={(event)=>{
              setUsuario(event.target.value);
            }}
            className="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1"/>
          </div>

          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Contrase帽a: </span>
            <input type="password" 
            onChange={(event)=>{
              setConstrasenia(event.target.value);
            }}
            className="form-control" placeholder="Password" aria-label="Username" aria-describedby="basic-addon1"/>
          </div>

        </div>

        <div className="card-footer text-muted">
            <button className='btn btn-success' onClick={send}>Enviar</button>
        </div>
        </div>

      <div className='lista'>
            <button onClick={getPersonas}>Listar</button>

      </div>

      <table className="table table-striped">

        <thead>
          <tr>
          <th scope="col">#</th>
            <th scope="col">Nombres</th>
            <th scope="col">Primer Apellido</th>
            <th scope="col">Segundo Apellido</th>
            <th scope="col">Nacionalidad</th>
          </tr>
        </thead>

        <tbody>
          {
            personaList.map((val,key)=>{
              return <tr key={val.persona_ID}>
              <th scope="row">{val.persona_ID}</th>
              <td>{val.nombres}</td>
              <td>{val.prim_apellido}</td>
              <td>{val.seg_apellido}</td>
              <td>{val.nacionalidad}</td>
              </tr>

            })
          }

        </tbody>

      </table>

    </div>
  );
}

export default App;
